module Jekyll  class RandomImageTag < Liquid::Tag    def initialize(tag_name, _text, _tokens)      super      @image_files = Dir.glob("assets/img/Overland_highlights/*")    end    def render(_context)      random_image = @image_files.sample      "<img src='#{random_image}' alt='Random Overland image' width='500' height='300'>"    end  endendLiquid::Template.register_tag('random_image', Jekyll::RandomImageTag)